<?php//// ����������� �������� ������.//include_once('models/M_User.php');class C_User extends C_Base{	//	// �����������.	//		public function action_auth(){		$this->title .= '::������';		$text = text_get();		$this->content = $this->Template('views/v_index.php', array('text' => $text));		/*$user = new M_User();		if($user->auth($login,$pass))		*/		}		public function action_edit(){		$this->title .= '::��������������';				if($this->isPost())		{			text_set($_POST['text']);			header('location: index.php');			exit();		}				$text = text_get();		$this->content = $this->Template('views/v_edit.php', array('text' => $text));	}}