<?phpinclude_once './models/M_User.php';class C_User extends C_Base{	public function action_auth(){		$this->title = 'Страница авторизации';        if(isset($_POST['login']) && isset($_POST['password'])){            $login = $_POST['login'];            $password = $_POST['password'];            $user = new M_User();            $res = $user ->authUsers($login, $password);            if($res){                session_start();                $success = true;                $_SESSION['login'] = $login;                $_SESSION['password'] = $password;                $this->content = $this->Template('views/v_auth.php', array('true' => $success));            } else {                $successFalse = true;                $this->content = $this->Template('views/v_auth.php', array('false' => $successFalse));            }        } else {            $this->content = $this->Template('views/v_auth.php');        }	}    public function action_reg(){        $this->title = 'Страница регистрации';        if(isset($_POST['login']) && isset($_POST['password']) && isset($_POST['email']) && isset($_POST['name'])){            $login = $_POST['login'];            $password  = $_POST['password'];            $email = $_POST['email'];            $name = $_POST['name'];            $user = new M_User();            $res = $user ->addUsers($login, $password, $email, $name);            if($res){                session_start();                $this->content = $this->Template('views/v_auth.php');            } else {                $successFalse = true;                $this->content = $this->Template('views/v_reg.php', array('false' => $successFalse));            }        } else {            $this->content = $this->Template('views/v_reg.php');        }    }    public function action_exit(){        session_start();        session_unset();        session_destroy();        $this->content = $this->Template('views/v_auth.php');    }}